Insert into FINAL_503_TEAM_PETPAL.FRIENDLY_BOARD (BOARD_CODE,BOARD_TITLE,BOARD_CONTENT,BOARD_VIEWS,BOARD_POST_DATE,BOARD_DELETE_YN,DECISION_CODE,USER_CODE,PLACE_NAME,PLACE_PHONE,PLACE_ADDRESS) values (15,'반려견 출입이 가능한 피자집, 범스 피자!','<div class="Story__Content Story__CardContent StoryContent" style="margin: 0px 0px 40px; padding: 0px; -webkit-tap-highlight-color: transparent; border: 0px none; font-family: SpoqaHanSans, &quot;Noto Sans SC&quot;, &quot;APPLE SD Gothic NEO&quot;, sans-serif; font-size: 16px;"><p class="StoryContent__Caption" style="margin-bottom: 0px; line-height: 1.611; text-align: center; padding: 0px; -webkit-tap-highlight-color: transparent; border: 0px none; font-size: 18px; letter-spacing: 0px; color: rgb(127, 127, 127); word-break: break-word;"><br></p><p class="StoryContent__Caption" style="margin-bottom: 0px; line-height: 1.611; text-align: center; padding: 0px; -webkit-tap-highlight-color: transparent; border: 0px none; font-size: 18px; letter-spacing: 0px; color: rgb(127, 127, 127); word-break: break-word;">용산의&nbsp;범스피자입니다.&nbsp;</p><p class="StoryContent__Caption" style="margin-bottom: 0px; line-height: 1.611; text-align: center; padding: 0px; -webkit-tap-highlight-color: transparent; border: 0px none; font-size: 18px; letter-spacing: 0px; color: rgb(127, 127, 127); word-break: break-word;">신용산역과&nbsp;삼각지역&nbsp;사이에&nbsp;자리하고&nbsp;있죠!&nbsp;</p><p class="StoryContent__Caption" style="line-height: 1.611; text-align: center; padding: 0px; -webkit-tap-highlight-color: transparent; border: 0px none; font-size: 18px; letter-spacing: 0px; color: rgb(127, 127, 127); word-break: break-word;">오픈한지&nbsp;얼마&nbsp;안&nbsp;된&nbsp;곳이지만,&nbsp;이미&nbsp;맛으로&nbsp;인정받고&nbsp;있어요.&nbsp;미국맛이면서도&nbsp;너무&nbsp;헤비하지&nbsp;않다는&nbsp;평.</p></div><p style="text-align: center;"><span style="color: rgb(127, 127, 127); font-family: SpoqaHanSans, &quot;Noto Sans SC&quot;, &quot;APPLE SD Gothic NEO&quot;, sans-serif; font-size: 18px;">귀요미&nbsp;''밥이''가&nbsp;출근하는&nbsp;범스피자는&nbsp;1인&nbsp;1견이&nbsp;원칙입니다.&nbsp;또,&nbsp;한&nbsp;테이블에는&nbsp;</span><span style="color: rgb(127, 127, 127); font-family: SpoqaHanSans, &quot;Noto Sans SC&quot;, &quot;APPLE SD Gothic NEO&quot;, sans-serif; font-size: 18px;">3마리까지만&nbsp;입장할&nbsp;수&nbsp;있다고&nbsp;해요.&nbsp;</span></p><p style="text-align: center;"><span style="color: rgb(127, 127, 127); font-family: SpoqaHanSans, &quot;Noto Sans SC&quot;, &quot;APPLE SD Gothic NEO&quot;, sans-serif; font-size: 18px;">매장이&nbsp;다소&nbsp;협소한&nbsp;편이라,&nbsp;반려견을&nbsp;케어하는&nbsp;견주와&nbsp;다른&nbsp;손님들의&nbsp;편의를&nbsp;모두&nbsp;고려해서&nbsp;내리신&nbsp;결정이라고&nbsp;하네요!&nbsp;</span></p><p style="text-align: center;"><span style="color: rgb(127, 127, 127); font-family: SpoqaHanSans, &quot;Noto Sans SC&quot;, &quot;APPLE SD Gothic NEO&quot;, sans-serif; font-size: 18px;">만약&nbsp;단체&nbsp;방문을&nbsp;원하시면&nbsp;대관을&nbsp;문의해보세요.</span></p><p style="text-align: center;"><img src="/petpal/resources/uploadFiles/d2d653b0138e49e486389abe26f27e3e.jpg" style="width: 600px;"><span style="color: rgb(127, 127, 127); font-family: SpoqaHanSans, &quot;Noto Sans SC&quot;, &quot;APPLE SD Gothic NEO&quot;, sans-serif; font-size: 18px;"><br></span></p>',0,to_date('21/07/13','RR/MM/DD'),'N',null,48,'서울특별시 용산구 한강로2가 62-2번지 1층','010-3849-3845','서울 용산구 한강로2가 62-2');
Insert into FINAL_503_TEAM_PETPAL.FRIENDLY_BOARD (BOARD_CODE,BOARD_TITLE,BOARD_CONTENT,BOARD_VIEWS,BOARD_POST_DATE,BOARD_DELETE_YN,DECISION_CODE,USER_CODE,PLACE_NAME,PLACE_PHONE,PLACE_ADDRESS) values (16,'관악구 강아지 동반 가능 카페','<p style="text-align: center; "><img src="/petpal/resources/uploadFiles/41dda85787c748b38d8fa53e652837ac.PNG" style="width: 25%;"><br></p><h5 style="text-align: center; ">저번주 주말에 관악구에 위치한 <b style="background-color: rgb(255, 255, 0);">몽캣</b>이라는 카페에 갔다왔어요</h5><h5 style="text-align: center; ">몽캣은 <b><font color="#000000" style="background-color: rgb(255, 255, 0);">카페&amp;맞춤옷</font></b> 공방으로 되어있고 반려동물의 맞춤옷을 컨셉으로 탄생한 공간이라고 하네요</h5><p style="text-align: center; ">몽캣은 다른 애견 카페처럼 입장료가 존재하지도 않고 음료 가격도 저렴해서 좋았습니다</p><p style="text-align: center; ">카페 뒷편에는 반려견 놀이터도 따로 마련되어있더라구요</p><p style="text-align: center; "><img src="/petpal/resources/uploadFiles/a0168308ea43483db07222500ab9d350.PNG" style="width: 25%;"><br></p><p style="text-align: center; ">다들 반려동물들이랑 같이 한번 방문해보세요~</p><p style="text-align: center; ">사장님도 친절하시고 너무 좋았습니다~!</p>',0,to_date('21/07/13','RR/MM/DD'),'N',null,27,'몽캣',' 070-5096-1327','서울 관악구 보라매로 14-3 (봉천동)');
Insert into FINAL_503_TEAM_PETPAL.FRIENDLY_BOARD (BOARD_CODE,BOARD_TITLE,BOARD_CONTENT,BOARD_VIEWS,BOARD_POST_DATE,BOARD_DELETE_YN,DECISION_CODE,USER_CODE,PLACE_NAME,PLACE_PHONE,PLACE_ADDRESS) values (17,'반려견 동반 브런치 카페, 헤이코다 입니다!! 강아지 전용 음식도 있어요 ㅎㅎ','<p style="text-align: center; "><img src="/petpal/resources/uploadFiles/688ccffdfcec46448cc3ca159ea176e5.png" style="width: 50%;"></p><p style="text-align: center; ">반려견 동반 브런치 카페인 헤이코다입니다!!</p><p style="text-align: center; ">오전 11시에 오픈해서 매일 10시까지 운영하고&nbsp;</p><p style="text-align: center; ">마지막 주문은 9시까지 받고 있습니다.&nbsp;</p><p style="text-align: center; "><br></p><p style="text-align: center; ">브런치카페이고요, 반려견을 위한 쿠키나 음식도 준비되어 있더라구요.</p><p style="text-align: center; ">미리 예약을 해주시면 뷰가 좋은 창가자리에 앉으실 수 있으니, 예약을 권장드릴게요!&nbsp;</p><p style="text-align: center; ">강아지들도 같이 방문했는데 맛있게 잘 먹더라구요</p>',0,to_date('21/07/13','RR/MM/DD'),'N',null,48,'서울특별시 강남구 논현1동 125-17','010-4920-4905','서울 강남구 논현동 125-17');
